## Select  
OptionSelect + button > Option`props``emit`:  
```html
<!--child.vue-->
<script lang="tsx">
import { Refresh } from '@element-plus/icons-vue'
import axios, { AxiosRequestConfig } from 'axios';
import { defineComponent, ref, reactive, onMounted } from "vue"
export default defineComponent({
    props: ['id'], // prop
    setup(props, { emit }) {
        let id = props.id
        interface optionsProps {
            label: string
            value: string
        }
        const options = reactive<optionsProps[]>([])
        //
        const getOptionsList = async () => {
            options.splice(0, options.length)
            const config: AxiosRequestConfig = {
                method: 'get',
                url: '/example',
                headers: {}
            };
            let data = await axios(config)
            let res = data.data
            res.map((item: string) => {
                if (item !== id) {
                    options.push({
                        label: item,
                        value: item
                    })
                }
            })
        }
        const selectValue = ref('')
        //Select
        const selectChange = (val: string) => {
            emit("getSelect", val)
        }
        onMounted(() => {
            getOptionsList()
        })
        return () => (
            <div class="container">
                //Vuetsx
                <el-select v-model={selectValue.value} onChange={selectChange}>
                    // v-for
                    {
                        options.map((item) => {
                            return <el-option
                                key={item.value}
                                label={item.label}
                                value={item.value}
                            />
                        })
                    }
                </el-select>
                <el-button icon={Refresh} size="large" onClick={getOptionsList} circle class="btn" />
            </div>
        )
    }
})
</script>

<style scoped>
.container {
    display: flex;
    align-items: center;
    justify-content: center;
}

.btn {
    margin-left: 10px;
}
</style>
```

