                        options.map((item) => {
                            return <el-option
                                key={item.value}
                                label={item.label}
                                value={item.value}
                            />
                        })
                    }
                </el-select>
                <el-button icon={Refresh} size="large" onClick={getOptionsList} circle class="btn" />
            </div>
        )
    }
})
</script>

<style scoped>
.container {
    display: flex;
    align-items: center;
    justify-content: center;
}

.btn {
    margin-left: 10px;
}
</style>
